    private void readJTable() {
    DefaultTableModel modelo = (DefaultTableModel) jTable.getModel();

    // Ajuste de largura das colunas (mude conforme necessário)
    jTable.getColumnModel().getColumn(0).setPreferredWidth(10);  // idMovimento
    jTable.getColumnModel().getColumn(1).setPreferredWidth(50);  // dtMovimento
    jTable.getColumnModel().getColumn(2).setPreferredWidth(30);  // idReduzida
    jTable.getColumnModel().getColumn(3).setPreferredWidth(100); // dsHistorico
    jTable.getColumnModel().getColumn(4).setPreferredWidth(40);  // vlMovimento
    jTable.getColumnModel().getColumn(5).setPreferredWidth(20);  // dsDC

    modelo.setNumRows(0);

    DataSource dataSource = new DataSource();
    DaoMovimentacao daoMov = new DaoMovimentacao(dataSource);

    for (model.Movimentacao m : daoMov.readAll()) {
        modelo.addRow(new Object[]{
            m.getIdMovimento(),
            m.getDtMovimento(),
            m.getIdReduzida(),
            m.getDsHistorico(),
            m.getVlMovimento(),
            m.getDsDC()
        });
    }

    dataSource.closeDataSource();
}

private void preencheJTable(String campo) {
    DefaultTableModel modelo = (DefaultTableModel) jTable.getModel();

    jTable.getColumnModel().getColumn(0).setPreferredWidth(10);  // idMovimento
    jTable.getColumnModel().getColumn(1).setPreferredWidth(50);  // dtMovimento
    jTable.getColumnModel().getColumn(2).setPreferredWidth(30);  // idReduzida
    jTable.getColumnModel().getColumn(3).setPreferredWidth(100); // dsHistorico
    jTable.getColumnModel().getColumn(4).setPreferredWidth(40);  // vlMovimento
    jTable.getColumnModel().getColumn(5).setPreferredWidth(20);  // dsDC

    modelo.setNumRows(0);

    DataSource dataSource = new DataSource();
    DaoMovimentacao daoMov = new DaoMovimentacao(dataSource);

    for (model.Movimentacao m : daoMov.consulta(campo)) {
        modelo.addRow(new Object[]{
            m.getIdMovimento(),
            m.getDtMovimento(),
            m.getIdReduzida(),
            m.getDsHistorico(),
            m.getVlMovimento(),
            m.getDsDC()
        });
    }

    dataSource.closeDataSource();
}

