    private void btnPesquisarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarActionPerformed
        String campo = jtfPesquisar.getText();

        if (campo.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Preencha o identificador da movimentação!");
        } else {
            DataSource dataSource = new DataSource();
            DaoMovimentacao daoMov = new DaoMovimentacao(dataSource);

            for (Movimentacao m : daoMov.consulta(campo)) {
                txtIdMovimentacao.setText(String.valueOf(m.getIdMovimento()));
                txtMovimento.setText(m.getDtMovimento());
                txtIdReduzida.setText(String.valueOf(m.getIdReduzida()));
                txtHistorico.setText(m.getDsHistorico());
                txtVlMovimento.setText(String.valueOf(m.getVlMovimento()));
                txtDC.setText(m.getDsDC());
            }

            dataSource.closeDataSource();
        }

    }//GEN-LAST:event_btnPesquisarActionPerformed

