    private void ALTERARActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ALTERARActionPerformed
        int codigo = Integer.parseInt(txtIdMovimentacao.getText());

        if (codigo <= 0) {
            JOptionPane.showMessageDialog(null, "Movimento não selecionado para alterar!");
        } else {
            // cria um datasource para conexão com o banco
            DataSource dataSource = new DataSource();

            // instancia um objeto de movimentação
            Movimentacao mov = new Movimentacao();
            DaoMovimentacao dao = new DaoMovimentacao(dataSource);

            // pega os dados do formulário
            mov.setIdMovimento(Integer.parseInt(txtIdMovimentacao.getText()));
            mov.setDtMovimento(txtMovimento.getText());
            mov.setIdReduzida(Integer.parseInt(txtIdReduzida.getText()));
            mov.setDsHistorico(txtHistorico.getText());
            mov.setVlMovimento(Double.parseDouble(txtVlMovimento.getText()));
            mov.setDsDC(txtDC.getText());

            // executa a alteração que está no DaoMovimentacao
            dao.alterar(mov);

            this.dispose();
        }


    }//GEN-LAST:event_ALTERARActionPerformed

