    private void btnPesquisarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarActionPerformed
        String campo = jtfPesquisar.getText();
        if (campo.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Preencha o campo para pesquisar");
        }
        else {
            DataSource dataSource = new DataSource();
            DaoPlanoDeContas daoPlano = new DaoPlanoDeContas(dataSource);

            for (PlanoDeContas c : daoPlano.consulta(campo)) {
                txtIdConta.setText(c.getIdConta());
                txtIdReduzida.setText(String.valueOf(c.getIdReduzida()));
                txtDescricao.setText(c.getDsDescricao());
                txtVlExAnterior.setText(String.valueOf(c.getVlExAnterior()));
                txtExAnteriorDC.setText(c.getDsExAnteriorDC());
                txtVlExAtual.setText(String.valueOf(c.getVlExAtual()));
                txtExAtualDC.setText(c.getDsExAtualDC());
                txtVlMesAnterior.setText(String.valueOf(c.getVlMesAnterior()));
                txtMesAnteriorDC.setText(c.getDsMesAnteriorDC());
                txtVlMesAtual.setText(String.valueOf(c.getVlMesAtual()));
                txtMesAtualDC.setText(c.getDsMesAtualDC());
            }
            dataSource.closeDataSource();
        }   
    }//GEN-LAST:event_btnPesquisarActionPerformed

