    private void EXCLUIRActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EXCLUIRActionPerformed
        String codigo = txtIdConta.getText();

        if (codigo == null || codigo.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Plano de Contas não selecionado para excluir!");
        } 
        else {
            int opcao = JOptionPane.showConfirmDialog(null,"Deseja excluir?","Atenção",JOptionPane.YES_OPTION);

            if(opcao == JOptionPane.YES_OPTION){
                DataSource dataSource = new DataSource();

                PlanoDeContas conta = new PlanoDeContas();
                DaoPlanoDeContas dao = new DaoPlanoDeContas(dataSource);

                //int codigo = Integer.parseInt(jTextField1.getText());
                dao.excluir(codigo);
            }

            this.dispose();
        }
    }//GEN-LAST:event_EXCLUIRActionPerformed

